mapper.select:
SELECT *
FROM Member
WHERE Member.ID = #{param1}
  AND Member.PASSWORD = #{param2}

SELECT Member.NICKNAME
FROM Member
WHERE Member.ID = #{id}

mapper.update:
UPDATE Member SET
  PASSWORD = #{password},
  NICKNAME = #{nickname},
  PROFILEPICTURE = #{profilePicture},
  RATING = #{rating}
  EMAIL = #{email}
  NAME = #{name}
  PHONE = #{phone}
WHERE ID = #{id}
