mapper.select:
SELECT SELLERID
FROM SECONDHAND
WHERE SHPOSTID = #{postId}

SELECT *
FROM Member
WHERE Member.ID = #{id}
AND Member.PASSWORD = #{password}

SELECT Member.NICKNAME
FROM Member
WHERE Member.ID = #{id}

SELECT Member.RATING
FROM Member
WHERE Member.ID = #{id}

mapper.update:
UPDATE Member SET
RATING = #{newRate}
WHERE ID = #{id}
